import { Segment, Header, Form, Table } from "semantic-ui-react";

import "../../shared/layout.css";
// import _ from 'lodash'

class Accounting extends Component {
    constructor(props) {
        super(props);
        this.state = {};
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick = (id) => {
        // this.setState({ activeItem: name })
        // console.log(id)
        console.log(id);
    };
    render() {

        return (

            <Segment>

                <Header floated="left" as="h3" style={{ marginTop: "6px" }} dividing>
                    IEA Modules (iShine Cloud: iSC)
                </Header>

                <Form>

                    {/* part three */}
                    <Header floated="left" as="h4" style={{ marginTop: "6px" }} >
                        3. Accounting â€“ QuickBooks Online Accounting Solution integrated with iSCHR (iSC Accounting)
                    </Header>

                    <Table celled>
                        <Table.Body>
                            <Table.Row>
                                <Table.Cell>
                                    <Form.TextArea label='Business Requirements' placeholder='' />
                                </Table.Cell>
                            </Table.Row>
                            <Table.Row>
                                <Table.Cell>
                                    <Form.TextArea label='Technical Requirements: data flow diagrams, UML or entity relationship diagrams ' placeholder='' />
                                </Table.Cell>
                            </Table.Row>
                            <Table.Row>
                                <Table.Cell>
                                    <Form.TextArea label='Support Provided' placeholder='' />
                                </Table.Cell>
                            </Table.Row>
                        </Table.Body>
                    </Table>

                    <Form.Button>Save Changes</Form.Button>
                </Form>
            </Segment>
        );
    }
}

export default Accounting;
