import React, { Component } from "react";
import { Segment, Header, Image, Form, Table } from "semantic-ui-react";

import "../../shared/layout.css";
// import _ from 'lodash'

class Recommendation extends Component {
    constructor(props) {
        super(props);
        this.state = {};
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick = (id) => {
        // this.setState({ activeItem: name })
        // console.log(id)
        console.log(id);
    };
    render() {

        return (

            <Segment>

                <Header floated="left" as="h3" style={{ marginTop: "6px" }} dividing>
                    Summary of the Recommendations
                </Header>

                <p style={{ marginTop: "48px", fontSize: "15px" }}>
                    A summary of the recommendations made against business requirements may be documented here.
                </p>

                <div>
                    <Form>
                        <Table celled>
                            <Table.Header>
                                <Table.Row>
                                    <Table.HeaderCell >
                                        Summary of Recommendations Made
                                    </Table.HeaderCell>
                                </Table.Row>
                            </Table.Header>

                            <Table.Body>
                                <Table.Row>
                                    <Table.Cell>
                                        <Form.TextArea label='' placeholder='' style={{ height: '200px' }} />
                                    </Table.Cell>
                                </Table.Row>
                            </Table.Body>
                        </Table>
                        <Form.Button style={{ marginLeft: "480px" }}>Submit</Form.Button>

                    </Form>
                </div>

            </Segment>
        );
    }
}

export default Recommendation;
