import React, { Component } from "react";
import { Container, Divider, Dropdown, Grid, Header, Image, List, Segment, } from 'semantic-ui-react'
import Nav from "../../components/shared/Nav";

import "../shared/layout.css";
// import _ from 'lodash'

class ProfilePage extends Component {
    constructor(props) {
        super(props);
        this.state = {};
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick = (id) => {
        // this.setState({ activeItem: name })
        // console.log(id)
        console.log(id);
    };



    render() {

        return (
            <Grid>
                <Grid.Row columns={3}>
                    <Grid.Column floated="left" attached>
                        <Nav name="profile" />
                    </Grid.Column>

                    {/* <Grid.Column floated="left" className="sub-nav" width={1}>
                        <SubNav name="profile/info" />
                    </Grid.Column> */}

                    <Grid.Column floated="left" className="profile-main-panel" width={16}>
                        <Grid.Row className="panel-header" fluid>
                            <Header as="h2" content="Personal Profile" dividing />
                        </Grid.Row>

                        <Header as='h1'>Semantic UI React Fixed Template</Header>
                        <p>This is a basic fixed menu template using fixed size containers.</p>
                        <p>
                            A text container is used for the main container, which is useful for single column layouts.
                            </p>

                        <Image src='https://react.semantic-ui.com/images/wireframe/media-paragraph.png' style={{ marginTop: '2em' }} />
                        <Image src='/images/wireframe/paragraph.png' style={{ marginTop: '2em' }} />
                        <Image src='/images/wireframe/paragraph.png' style={{ marginTop: '2em' }} />
                        <Image src='/images/wireframe/paragraph.png' style={{ marginTop: '2em' }} />
                        <Image src='/images/wireframe/paragraph.png' style={{ marginTop: '2em' }} />
                        <Image src='/images/wireframe/paragraph.png' style={{ marginTop: '2em' }} />
                        <Image src='/images/wireframe/paragraph.png' style={{ marginTop: '2em' }} />


                        <Grid.Row>
                        </Grid.Row>

                    </Grid.Column>
                </Grid.Row>
            </Grid>
        );
    }
}

export default ProfilePage;
