import React from 'react';
import { Modal, Table, Form, Button, Header } from 'semantic-ui-react';
import DateSelector from "./date-selector";



function ModalIndustry(props) {
    const [open, setOpen] = React.useState(false)

    // const students = [
    //     'Anthony',
    //     'Keira',
    //     'Nicholas'
    // ]

    return (
        <Modal
            onClose={() => setOpen(false)}
            onOpen={() => setOpen(true)}
            open={open}
            // trigger={
            //     <>
            //         {
            //             students.map((student) => (
            //                 <Table.Row>
            //                     <Table.Cell> {student}</Table.Cell>
            //                     <Table.Cell textAlign="center">Pending</Table.Cell>
            //                     <Table.Cell textAlign="center">Pending</Table.Cell>
            //                 </Table.Row>))
            //         }
            //     </>

            // }
            trigger={
                <Table.Row>
                    <Table.Cell> {props.student}</Table.Cell>
                    <Table.Cell textAlign="center">Pending</Table.Cell>
                    <Table.Cell textAlign="center">Pending</Table.Cell>
                </Table.Row>
            }
        >
            <Modal.Header>Overall Review by Industrial Supervisor</Modal.Header>
            <Modal.Content>
                <Modal.Description>
                    <p>
                        Use the scale below to evaluate the participant’s performance during the placement. Insert “NA” if the item evaluated is not applicable or not enough information is available to form a judgment.
                    </p>

                    <Form>
                        <Header as="h4">Review</Header>
                        <Form.TextArea placeholder='' />

                        <Header as="h4">Justification of Eligibility of Micro-credentials in Various Domains </Header>
                        <Form.TextArea placeholder='' />
                        <Form.Group>
                            <Form.Field>
                                <label>Name</label>
                                {/* <DateSelector /> */}
                                <input placeholder='' />
                            </Form.Field>
                            <Form.Field>
                                <label>Date</label>
                                <DateSelector />
                            </Form.Field>
                        </Form.Group>
                    </Form>
                </Modal.Description>
            </Modal.Content>
            <Modal.Actions>
                <Button color='black' onClick={() => setOpen(false)}>
                    Cancel
        </Button>
                <Button
                    content="Submit"
                    labelPosition='right'
                    icon='upload'
                    onClick={() => setOpen(false)}
                    positive
                />
            </Modal.Actions>
        </ Modal>
    )
}

export default ModalIndustry;
