import React, { Component } from "react";
import { Segment, Grid, Accordion, Header, Menu, List, Image, Form, Table } from "semantic-ui-react";
import Nav from "../../components/shared/Nav";
import SubNav from "./SubNav";
import CustomerPersona from "./component/persona";
import SalesProposal from "./component/sales";
import ClientJourney from "./component/journey"
import CriteriaOne from "./component/criteriaOne"


import "../shared/layout.css";
// import _ from 'lodash'

class ClientAcquisitionPage extends Component {
  constructor(props) {
    super(props);
    this.state = { activeItem: 'customer-persona' };
    this.handleItemClick = this.handleItemClick.bind(this);
  }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })

  render() {

    const { activeItem } = this.state

    function LearningPanel(props) {

      activeItem = props.activeItem;

      if (activeItem === 'customer-persona') {
        return <CustomerPersona />;
      }
      if (activeItem === 'sales-proposal') {
        return <SalesProposal />;
      }
      if (activeItem === 'client-journey') {
        return <ClientJourney />;
      }
      if (activeItem === 'measurement-criteria') {
        return <CriteriaOne />;
      }
    }

    return (
      <Grid>
        <Grid.Row columns={3}>
          <Grid.Column floated="left" attached>
            <Nav name="learning" />
          </Grid.Column>

          <Grid.Column floated="left" className="sub-nav" width={1}>
            <SubNav name="learning/client-acquisition" />
          </Grid.Column>

          <Grid.Column floated="left" className="main-panel" width={16}>
            <Grid.Row className="panel-header" fluid>
              <Header as="h2" content="Client Acquisition" dividing />
            </Grid.Row>

            <Grid.Row>

              <Segment className="general-introduction" style={{ height: "160px" }}>
                <Header floated="left" as="h4" dividing style={{ marginTop: "0px" }}>
                  General Introduction
                  </Header>
                <p style={{ marginTop: "40px", fontSize: "15px" }}>
                  This involves two processes:
                    </p>
                <List bulleted style={{ marginLeft: "40px", fontSize: "15px" }}>
                  <List.Item>Outreach and engagement, that is, to organise clinics, events and talks, to engage Charities
                        </List.Item>
                  <List.Item>Client relationship and contact management, that is, managing the clientâ€™s needs and contracts
                        </List.Item>
                </List>
              </Segment>

              <Segment className="learning-panel">
                <Menu attached='top' tabular>
                  <Menu.Item
                    name='customer-persona'
                    active={activeItem === 'customer-persona'}
                    onClick={this.handleItemClick}
                  />
                  <Menu.Item
                    name='sales-proposal'
                    active={activeItem === 'sales-proposal'}
                    onClick={this.handleItemClick}
                  />
                  <Menu.Item
                    name='client-journey'
                    active={activeItem === 'client-journey'}
                    onClick={this.handleItemClick}
                  />
                  <Menu.Item
                    name='measurement-criteria'
                    active={activeItem === 'measurement-criteria'}
                    onClick={this.handleItemClick}
                  />
                </Menu>

                <LearningPanel activeItem={activeItem} />
                <CustomerPersona />
                <SalesProposal />
                <ClientJourney />
                <CriteriaOne />

              </Segment>
            </Grid.Row>
          </Grid.Column>
        </Grid.Row>
      </Grid>
    );
  }
}

export default ClientAcquisitionPage;
