import React, { Component } from "react";
import { Segment, Header, Form } from "semantic-ui-react";
import Editor from "./editor";

import "../../shared/layout.css";
// import _ from 'lodash'

class Description extends Component {
    constructor(props) {
        super(props);
        this.state = {};
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick = () => {
        // this.setState({ activeItem: name })
        // console.log(id)
        this.props.handleClick();
    };

    render() {

        return (

            <Segment>
                <Header floated="left" as="h3" style={{ marginTop: "6px" }} dividing>
                    Describe your Job Description?
                </Header>

                <p style={{ marginTop: "48px", fontSize: "15px" }}>
                    Identify the purpose of your role, key tasks, and main accountabilities of your job.
                </p>

                <Form>
                    <Editor />
                    <div className="button-row">
                        <Form.Button
                            onClick={this.handleOneSubmit}
                            disabled={this.state.buttonOneCompleted}
                            className="submit-button"
                            content="Submit"
                            labelPosition="right"
                            icon='upload'
                            color="green"
                            style={{ marginLeft: "0px" }}
                        />
                        <Form.Button
                            onClick={this.handleOneSubmit}
                            disabled={this.state.buttonOneCompleted}
                            className="submit-button"
                            content="Edit"
                            labelPosition="right"
                            icon='edit'
                            color="green"
                            style={{ marginLeft: "160px" }}

                        />
                    </div>
                </Form>
            </Segment>
        );
    }
}

export default Description;
