import React, { Component } from "react";
import { Segment, Header, Form } from "semantic-ui-react";
import Editor from "./editor";

import "../../shared/layout.css";
// import _ from 'lodash'

class Risk extends Component {
    constructor(props) {
        super(props);
        this.state = {};
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick = () => {
        // this.setState({ activeItem: name })
        // console.log(id)
        this.props.handleClick();
    };

    render() {

        return (

            <Segment>
                <Header floated="left" as="h3" style={{ marginTop: "6px" }} dividing>
                    Assess the risks to implement the recommended solutions.
                </Header>

                <p style={{ marginTop: "48px", fontSize: "15px" }}>
                    Take into account the issues of business/operational uncertainty, technical uncertainty and environmental uncertainty that may affect your recommended solution. State your comments here.
                </p>

                <Form>
                    <Editor />
                    <div className="button-row">
                        <Form.Button onClick={this.handleEightSubmit} disabled={this.state.buttonEightCompleted} className="submit-button">Submit</Form.Button>
                        <Form.Button onClick={this.handleEightEdit} disabled={!this.state.buttonEightCompleted} className="edit-button">Edit</Form.Button>
                    </div>
                </Form>
            </Segment>
        );
    }
}

export default Risk;
