import React, { Component } from "react";
import { Segment, Grid, Header, Menu, List } from "semantic-ui-react";
import Nav from "../../components/shared/Nav";
import SubNav from "./SubNav";
import General from "./componentTwo/general";
import ManagedServices from "./componentTwo/managed";
import Human from "./componentTwo/human";
import IntroTwo from "./componentTwo/intro";
import Accounting from "./componentTwo/accounting";
import Donation from "./componentTwo/donation";
import Volunteer from "./componentTwo/volunteer";
import Recommendation from "./componentTwo/recommendations";
import CriteriaTwo from "./componentTwo/criteriaTwo";


import "../shared/layout.css";
// import _ from 'lodash'

class SolutionsSupportPage extends Component {
  constructor(props) {
    super(props);
    this.state = { activeItem: 'general-introduction' };
    this.handleItemClick = this.handleItemClick.bind(this);
  }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })

  render() {

    const { activeItem } = this.state

    function LearningPanel(props) {

      const activeItem = props.activeItem;

      if (activeItem === 'introduction') {
        return <IntroTwo />;
        // return <IntroTwo handleClick={handleIntro} />;
      }
      if (activeItem === 'general-services') {
        return <General />;
        // return <General handleClick={handleGeneral} />;
      }
      if (activeItem === 'human-resources') {
        return <Human />;
        // return <Human handleClick={handleHuman} />;
      }
      if (activeItem === 'accounting') {
        return <Accounting />;
        // return <Accounting handleClick={handleAccounting} />;
      }
      if (activeItem === 'donation-services') {
        return <Donation />;
        // return <Donation handleClick={handleDonation} />;
      }
      if (activeItem === 'volunteer-services') {
        return <Volunteer />;
        // return <Volunteer handleClick={handleVolunteer} />;
      }
      if (activeItem === 'managed-services') {
        return <ManagedServices />;
        // return <ManagedServices handleClick={handleManaged} />;
      }
      if (activeItem === 'recommendations') {
        return <Recommendation />;
        // return <Recommendation handleClick={handleRecommendation} />;
      }
      if (activeItem === 'measurement-criteria') {
        return <CriteriaTwo />;
        // return <CriteriaTwo handleClick={handleCriteria} />;
      }
    }

    return (
      <Grid>
        <Grid.Row columns={3}>
          <Grid.Column floated="left" attached>
            <Nav name="learning" />
          </Grid.Column>

          <Grid.Column floated="left" className="sub-nav" width={1}>
            <SubNav name="learning/solutions-support" />
          </Grid.Column>

          <Grid.Column floated="left" className="main-panel" width={16}>
            <Grid.Row className="panel-header" fluid>
              <Header as="h2" content="Client Acquisition" dividing />
            </Grid.Row>

            <Grid.Row>

              <Segment className="general-introduction" style={{ height: "160px" }}>
                <Header floated="left" as="h4" dividing style={{ marginTop: "0px" }}>
                  General Introduction
                  </Header>
                <p style={{ marginTop: "40px", fontSize: "15px" }}>
                  This involves two processes:
                    </p>
                <List bulleted style={{ marginLeft: "40px", fontSize: "15px" }}>
                  <List.Item>Outreach and engagement, that is, to organise clinics, events and talks, to engage Charities
                        </List.Item>
                  <List.Item>Client relationship and contact management, that is, managing the client’s needs and contracts
                        </List.Item>
                </List>
              </Segment>

              <Segment className="learning-panel">
                <Menu attached='top' tabular>
                  <Menu.Item
                    name='customer-persona'
                    active={activeItem === 'customer-persona'}
                    onClick={this.handleItemClick}
                  />
                  <Menu.Item
                    name='sales-proposal'
                    active={activeItem === 'sales-proposal'}
                    onClick={this.handleItemClick}
                  />
                  <Menu.Item
                    name='client-journey'
                    active={activeItem === 'client-journey'}
                    onClick={this.handleItemClick}
                  />
                  <Menu.Item
                    name='measurement-criteria'
                    active={activeItem === 'measurement-criteria'}
                    onClick={this.handleItemClick}
                  />
                </Menu>


                <Segment attached='bottom'>
                  <img src='https://react.semantic-ui.com/images/wireframe/paragraph.png' />
                </Segment>
              </Segment>
            </Grid.Row>
          </Grid.Column>
        </Grid.Row>
      </Grid>
    );
  }
}

export default SolutionsSupportPage;
