import React, { Component } from "react";
import { Segment, Form } from "semantic-ui-react";

import "./layout.css";
// import _ from 'lodash'

class InfoDisplay extends Component {
    constructor(props) {
        super(props);
        this.state = { currentStep: props.activeStep };
        this.handleClick = this.handleClick.bind(this);
        // this.handleChange = this.handleChange.bind(this);
    }

    handleClick = () => {
        // this.setState((state) => ({
        //     currentStep: state.currentStep + 1
        // }));
        this.props.handleEdit();
        window.scrollTo(0, 0)
    };

    render() {

        // const { currentStep } = this.state;

        // function StepButton(props) {
        //     const currentStep = props.currentStep;
        //     const handleClick = props.handleClick;
        //     if (currentStep === 2) {
        //         return <Form.Button onClick={handleClick}>Confirm & Submit</Form.Button>
        //     }
        //     if (currentStep > 2) {
        //         return <Form.Button onClick={handleClick}>Edit</Form.Button>
        //     }
        // }

        return (
            <Form>
                <Segment style={{ marginTop: "20px" }}>
                    <Form.Field fluid>
                        <label>Name of Student:</label>
                        <input value='Anthony' readOnly />
                    </Form.Field>
                    <Form.Field fluid>
                        <label>Student No: </label>
                        <input value='Anthony' readOnly />
                    </Form.Field>

                    <Form.Input fluid label='Student No: ' value='1000023' />
                    <Form.Input fluid label='Mobile No:  ' value='88888888' />
                    <Form.Input fluid label='Email: ' value='abcd@gmail.com' />
                </Segment>

                <Segment style={{ marginTop: "10px" }}>
                    <Form.Input fluid label='Partner Organization: ' value='Organization name' />
                    <Form.Input fluid label='Period of Placement: ' value='2 months' />
                    <Form.Input fluid label='Name of Industrial Supervisor(s): ' value='Jack' />
                    <Form.Input fluid label='Position of Industrial Supervisor(s): ' value="Senior executive" />
                    <Form.Input fluid label='Contact No of Industrial Supervisor(s): ' value='99999999' />
                </Segment>

                <Segment style={{ marginTop: "10px" }}>
                    <Form.Input fluid label='Name of Academic Supervisor(s): ' value='Tony' />
                    <Form.Input fluid label='Contact No of Academic Supervisor(s): ' value='77777777' />
                </Segment>

                {/* <StepButton currentStep={currentStep} handleClick={this.handleClick} /> */}

                <Form.Button onClick={this.handleClick}>Edit</Form.Button>
            </Form>
        );
    }
}

export default InfoDisplay;
