import React, { Component } from "react";
import { Form } from "semantic-ui-react";

import "../../shared/layout.css";
// import _ from 'lodash'

class ButtonClick extends Component {
    constructor(props) {
        super(props);
        this.state = {};
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick = (e, { buttonSubmit }) => {
        this.props.handleButton(buttonSubmit);
    };


    render() {

        const { buttonSubmit } = this.props.buttonSubmit;

        function PanelButton(props) {
            const buttonSubmit = props.buttonSubmit;
            const handleClick = props.handleClick;
            if (buttonSubmit === true) {
                return <Form.Button buttonSubmit={buttonSubmit} onClick={handleClick} style={{ marginLeft: "480px" }}>Submit</Form.Button>;
            }
            if (buttonSubmit === false) {
                return <Form.Button buttonSubmit={buttonSubmit} onClick={handleClick} style={{ marginLeft: "480px" }}>Edit</Form.Button>;
            }
        }

        return (

            <PanelButton buttonSubmit={buttonSubmit} handleClick={this.handleClick} />
        );
    }
}

export default ButtonClick;
