import React, { Component } from "react";
import { Segment, Grid, Header, Menu, List } from "semantic-ui-react";
import Nav from "../../components/shared/Nav";
import SubNav from "./SubNav";

import "../shared/layout.css";
// import _ from 'lodash'

class AgilePage extends Component {
  constructor(props) {
    super(props);
    this.state = { activeItem: 'bio' };
    this.handleItemClick = this.handleItemClick.bind(this);
  }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })

  render() {

    const { activeItem } = this.state

    return (
      <Grid>
        <Grid.Row columns={3}>
          <Grid.Column floated="left" attached>
            <Nav name="learning" />
          </Grid.Column>

          <Grid.Column floated="left" className="sub-nav" width={1}>
            <SubNav name="learning/agile" />
          </Grid.Column>

          <Grid.Column floated="left" className="main-panel" width={16}>
            <Grid.Row className="panel-header" fluid>
              <Header as="h2" content="Agile Problem-Solving and Innovation" dividing />
            </Grid.Row>

            <Grid.Row>

              <Segment className="general-introduction" style={{ height: "160px" }}>
                <Header floated="left" as="h3" dividing style={{ marginTop: "0px" }}>
                  General Introduction
                  </Header>
                <p style={{ marginTop: "40px", fontSize: "15px" }}>
                  The main objective of this module is to enable participants to experience the challenges of problem-solving and innovation through an agile, flexible and team-based approach.
                </p>
                <p style={{ fontSize: "15px" }}>
                  At the end of the module, participants would be able
                </p>
                <List bulleted style={{ marginLeft: "40px", fontSize: "15px" }}>
                  <List.Item>
                    Experience the context of challenges in the work environment, resolve at least one problem and suggest an innovation or prototype solution to overcome the problem.
                  </List.Item>
                  <List.Item>Experience the context of challenges in the work environment, resolve at least one problem and suggest an innovation or prototype solution to overcome the problem.
                  </List.Item>
                </List>
              </Segment>


            </Grid.Row>
          </Grid.Column>
        </Grid.Row>
      </Grid>
    );
  }
}

export default AgilePage;
