import React, { Component } from "react";
import { Segment, Header, List, Button } from "semantic-ui-react";

import "../../shared/layout.css";
// import _ from 'lodash'

class IntroOne extends Component {
    constructor(props) {
        super(props);
        this.state = {};
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick = () => {
        // this.setState({ activeItem: name })
        // console.log(id)
        this.props.handleClick();
    };

    render() {

        return (

            <Segment className="general-introduction" style={{ height: "260px" }}>
                <Header floated="left" as="h3" dividing style={{ marginTop: "0px" }}>
                    General Introduction
                </Header>
                <p style={{ marginTop: "45px", fontSize: "15px" }}>
                    The main objective of this module is to enable participants to experience the challenges of problem-solving and innovation through an agile, flexible and team-based approach.
                </p>
                <p style={{ fontSize: "15px" }}>
                    At the end of the module, participants would be able
                </p>
                <List bulleted style={{ marginLeft: "40px", fontSize: "15px" }}>
                    <List.Item>
                        Experience the context of challenges in the work environment, resolve at least one problem and suggest an innovation or prototype solution to overcome the problem.
                  </List.Item>
                    <List.Item>
                        Use the vehicle of empathy, person-centred and lean management approaches to understand stakeholdersâ€™ interests in solving the problem.
                  </List.Item>
                </List>
                <Button onClick={this.handleIntro} style={{ marginLeft: "450px" }}>Mark as Complete</Button>
            </Segment>
        );
    }
}

export default IntroOne;
