import React, { Component } from "react";
import { Segment, Grid, Step, Header } from "semantic-ui-react";
import Nav from "../../components/shared/Nav";
import SubNav from "./SubNav";

import "../shared/layout.css";
// import _ from 'lodash'

class ProfilePage extends Component {
    constructor(props) {
        super(props);
        this.state = {};
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick = (id) => {
        // this.setState({ activeItem: name })
        // console.log(id)
        console.log(id);
    };

    render() {
        const steps = [
            {
                key: 'shipping',
                completed: true,
                icon: 'truck',
                title: 'Shipping',
                description: 'Choose your shipping options',
            },
            {
                key: 'billing',
                active: true,
                icon: 'payment',
                title: 'Billing',
                description: 'Enter billing information',
            },
            {
                key: 'confirm',
                disabled: true,
                icon: 'info',
                title: 'Confirm Order',
                description: 'Please check your personal information'
            },
        ]

        return (
            <Grid>
                <Grid.Row columns={3}>
                    <Grid.Column floated="left" attached>
                        <Nav name="profile" />
                    </Grid.Column>

                    <Grid.Column floated="left" className="sub-nav" width={1}>
                        <SubNav name="profile/info" />
                    </Grid.Column>

                    <Grid.Column floated="left" className="main-panel" width={16}>
                        <Grid.Row className="panel-header" fluid>
                            <Header as="h2" content="Programme Information" dividing />
                        </Grid.Row>

                        <Grid.Row>
                            <Step.Group items={steps} />
                        </Grid.Row>
                    </Grid.Column>
                </Grid.Row>
            </Grid>
        );
    }
}

export default ProfilePage;

